mapper.select:
SELECT COUNT(1)
FROM TB_BOOKMARK C
WHERE C.TARGET_TYPE = #{target_type}
	AND C.TARGET_CD = #{target_cd}
	AND C.USER_CD = #{_SESSION_USER_CD_}

mapper.delete:
DELETE FROM TB_BOOKMARK C
WHERE C.TARGET_TYPE = #{target_type}
	AND C.TARGET_CD = #{target_cd}
	AND C.USER_CD = #{_SESSION_USER_CD_}
