mapper.select:
SELECT H.HABITAT_CD , H.HABITAT_NM , COALESCE(FN_GET_AREA_NAME(H.SIDO_CD), '') || ' ' || COALESCE(FN_GET_AREA_NAME(H.SIGUNGU_CD), '') || ' ' || COALESCE(FN_GET_AREA_NAME(H.DONG_CD), '') AS AREA_NM , H.ADDRESS , H.SIDO_CD , H.SIGUNGU_CD , H.DONG_CD , H.ADDRESS , H.HABITAT_NM , H.LAT , H.LNG , H.USER_CD , F.FILE_PATH FROM TB_HABITAT H LEFT JOIN TB_FILE F ON H.FILE_GRP_ID = F.FILE_GRP_ID
H.SIDO_CD = #{sido_cd}
AND H.SIGUNGU_CD = #{sigungu_cd}
AND H.DONG_CD = #{dong_cd}
AND H.HABITAT_CD = #{habitat_cd}
AND H.HABITAT_NM like '%'|| #{habitat_nm} ||'%'
AND ( SELECT COUNT(*) FROM TB_HABITAT_INFO I WHERE I.HABITAT_CD = H.HABITAT_CD AND I.CAT_CD = #{cat_cd} ) > 0
ORDER BY H.HABITAT_CD DESC

SELECT COUNT(*) 
FROM TB_HABITAT_INFO G
WHERE G.HABITAT_CD = #{habitat_cd}

mapper.insert:
    	INSERT INTO TB_HABITAT
		(
			SIDO_CD
			, SIGUNGU_CD
			, DONG_CD
			, ADDRESS
			, HABITAT_NM
			, LAT
			, LNG

			, FILE_GRP_ID

			, USER_CD
			, REG_ID
			, REG_DT
			, UPDT_ID
			, UPDT_DT
		)
		VALUES
		(
			#{sido_cd}
			, #{sigungu_cd}
			, #{dong_cd}
			, #{address}
			, #{habitat_nm}
			, #{lat}
			, #{lng}

			, #{file_grp_id}

			, #{_SESSION_USER_CD_} -- 입력사용자
			, #{_SESSION_USER_CD_} -- 입력사용자
			, NOW() -- 입력일자
			, #{_SESSION_USER_CD_} -- 변경사용자
			, NOW() -- 변경일자
		)

mapper.update:
    	UPDATE TB_HABITAT
		SET SIDO_CD = #{sido_cd}
			, SIGUNGU_CD = #{sigungu_cd}
			, DONG_CD = #{dong_cd}
			, ADDRESS = #{address}
			, HABITAT_NM = #{habitat_nm}
			, LAT = #{lat}
			, LNG = #{lng}
			, UPDT_ID = #{_SESSION_USER_CD_}
			, UPDT_DT = NOW()
		WHERE HABITAT_CD = #{habitat_cd}
